DROP DATABASE UseCaseDB;CREATE DATABASE UseCaseDB;USE UseCaseDB;/** This is taken care of by JPA*/CREATE TABLE `PRODUCT` (  `PID` int(11) NOT NULL AUTO_INCREMENT,  `PCATEGORY` varchar(255) DEFAULT NULL,  `PDESCRIPTION` varchar(500) DEFAULT NULL,  `PIMAGE` varchar(255) DEFAULT NULL,  `PNAME` varchar(255) DEFAULT NULL,  `PPRICE` double DEFAULT NULL,  `PSERVING` varchar(255) DEFAULT NULL,  `PSIZE` varchar(255) DEFAULT NULL,  `PSUMMARY` varchar(255) DEFAULT NULL,  `PWEIGHT` varchar(255) DEFAULT NULL,  PRIMARY KEY (`PID`)) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8mb4;CREATE TABLE `USER` (  `email` varchar(50) NOT NULL,  `firstName` varchar(50) DEFAULT NULL,  `lastName` varchar(50) DEFAULT NULL,  `password` varchar(50) NOT NULL,  PRIMARY KEY (`email`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;CREATE TABLE `USER_PRODUCT` (  `User_email` varchar(50) NOT NULL,  `uProducts_PID` int(11) NOT NULL,  PRIMARY KEY (`User_email`,`uProducts_PID`),  KEY `FK_USER_PRODUCT_uProducts_PID` (`uProducts_PID`),  CONSTRAINT `FK_USER_PRODUCT_User_email` FOREIGN KEY (`User_email`) REFERENCES `USER` (`email`),  CONSTRAINT `FK_USER_PRODUCT_uProducts_PID` FOREIGN KEY (`uProducts_PID`) REFERENCES `PRODUCT` (`PID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;//Populate User DataINSERT INTO usecaseDB.`USER` (firstName, lastName, email, password) VALUES("khan", "Johnson", "o@c.com", "bb");INSERT INTO usecaseDB.`USER` (firstName, lastName, email, password) VALUES("Clem", "Startin", "c@s.com", "pass");INSERT INTO usecaseDB.`USER` (firstName, lastName, email, password) VALUES("Thornie", "cs@abc.com", "ta@abc.com", "Attwool");INSERT INTO usecaseDB.`USER` (firstName, lastName, email, password) VALUES("Harcourt", "Guerre","hg@abc.com", "pass");INSERT INTO usecaseDB.`USER` (firstName, lastName, email, password) VALUES("Holmes", "Taffley", "ht@abc.com", "pass");INSERT INTO usecaseDB.`USER` (firstName, lastName, email, password) VALUES("Alexandra", "Iannitti"